custom_css:
/* Example CSS for your font: montserrat-variablefont_wght */

/* Apply to all text */
:root * {
	--font-default: 'montserrat-variablefont_wght';
	--default-font: 'montserrat-variablefont_wght';
	--font-family-editor: 'montserrat-variablefont_wght';
	--font-interface-override: 'montserrat-variablefont_wght';
	--font-text-override: 'montserrat-variablefont_wght';
}

/* Example: Apply to custom CSS class */
.custom-font * {
	font-family: 'montserrat-variablefont_wght' !important;
}

/* Example: Apply to specific elements only */
.custom-font h1, .custom-font h2, .custom-font h3 {
	font-family: 'montserrat-variablefont_wght' !important;
}
